const handleDelete = (id) => {
const newBlogs = blogs.filter(blog => blog.id !== id);
setBlogs(newBlogs);
}
    return ( 
        <div className="home">
            <BlogList blogs={blogs} title="All the Blogs!" handleDelete= {handleDelete}></BlogList>
        </div>
     );
}
 
export default Home;
